mapper.select:
SELECT COUNT(*) FROM ( SELECT HP.HP_NO, HP.HP_NAME, HP.HP_PHONE, HP.HP_POSTCODE, HP.HP_ADDRESS, HP.HP_AVG_RATE, HP.HP_REVIEW, HP.HP_STATUS, LISTAGG(CAST(HG.HPCATE_NAME AS VARCHAR(100)),', ')WITHIN GROUP (ORDER BY NULL) AS HPCATE_NAME FROM HOSPITAL HP JOIN HPLIST HL ON (HP.HP_NO = HL.HP_NO) JOIN HPCATEGORY HG ON (HL.HPCATE_CODE = HG.HPCATE_CODE)
AND HP.HP_NO=#{hpNo}
AND HP.HP_NAME LIKE '%' || #{hpName} || '%'
AND HG.HPCATE_CODE=#{hpCateCode}
AND HG.HPCATE_NAME LIKE '%' || #{hpCateName} || '%'
AND HP.STATUS=#{hpStatus}
GROUP BY HP.HP_NO, HP.HP_NAME, HP.HP_PHONE, HP.HP_POSTCODE, HP.HP_ADDRESS, HP.HP_AVG_RATE, HP.HP_REVIEW, HP.HP_STATUS
)

SELECT ROWNUM, HP_NO, HP_NAME, HP_PHONE, HP_POSTCODE, HP_ADDRESS, HP_AVG_RATE, HP_REVIEW, HP_STATUS, HPCATE_NAME FROM ( SELECT HP.HP_NO, HP.HP_NAME, HP.HP_PHONE, HP.HP_POSTCODE, HP.HP_ADDRESS, HP.HP_AVG_RATE, HP.HP_REVIEW, HP.HP_STATUS, LISTAGG(CAST(HG.HPCATE_NAME AS VARCHAR(100)),', ')WITHIN GROUP (ORDER BY NULL) AS HPCATE_NAME FROM HOSPITAL HP JOIN HPLIST HL ON (HP.HP_NO = HL.HP_NO) JOIN HPCATEGORY HG ON (HL.HPCATE_CODE = HG.HPCATE_CODE)
AND HP.HP_NO=#{hpNo}
AND HP.HP_NAME LIKE '%' || #{hpName} || '%'
AND HG.HPCATE_CODE=#{hpCateCode}
AND HG.HPCATE_NAME LIKE '%' || #{hpCateName} || '%'
AND HP.STATUS=#{hpStatus}
GROUP BY HP.HP_NO, HP.HP_NAME, HP.HP_PHONE, HP.HP_POSTCODE, HP.HP_ADDRESS, HP.HP_AVG_RATE, HP.HP_REVIEW, HP.HP_STATUS )

SELECT HP_DAY, HP_OPEN_TIME, HP_CLOSE_TIME
FROM HPTIME
WHERE HP_UNIQUENO = #{hpNo}

SELECT BE_HP_NO, BE_HP_NAME, BE_HP_PHONE, BE_HP_POSTCODE, BE_HP_ADDRESS, BE_HP_STATUS
FROM BEFORE_HPINFO
WHERE BE_HP_NO=#{hpNo}

mapper.update:
UPDATE HOSPITAL
SET HP_NAME = #{hpName}
	, HP_PHONE = #{hpPhone}
	, HP_POSTCODE = #{hpPostCode}
	, HP_ADDRESS = #{hpAddress}
	, HP_STATUS = 'Y'
WHERE HP_NO = #{hpNo}

UPDATE DRHPPHOTO
SET DRHP_ORIGIN = #{originFileName}
	, DRHP_RENAME = #{renameFileName}
	, DRHP_FILEPATH = 'NULL'
WHERE PH_CODE = 1
	AND HP_NO = #{hpNo}

UPDATE DRHPPHOTO
SET DRHP_ORIGIN = #{originFileName}
	, DRHP_RENAME = #{renameFileName}
	, DRHP_FILEPATH = 'NULL'
WHERE PH_CODE = 2
	AND HP_NO = #{hpNo}

UPDATE DRHPPHOTO
SET DRHP_ORIGIN = #{originFileName}
	, DRHP_RENAME = #{renameFileName}
	, DRHP_FILEPATH = 'NULL'
WHERE PH_CODE = 3
	AND HP_NO = #{hpNo}

mapper.delete:
UPDATE HOSPITAL
SET HP_STATUS = 'X'
WHERE HP_NO = #{hpNo}
