mapper.select:
     SELECT COUNT(*)
     FROM 
     (SELECT HR.HP_RE_NO, H.HP_NAME, HR.HPCATE_CODE, HG.HPCATE_NAME, C.C_NO, C.C_ID, HR.HP_NO, HR.HP_RE_DATE, HR.LIKE_YN, HR.HP_RE_AVG_RATE, HR.RECEIPT_YN
		FROM HPREVIEW HR
		    JOIN CLIENT C ON(HR.C_NO = C.C_NO)
		    JOIN HOSPITAL H ON (HR.HP_NO = H.HP_NO)
		    JOIN HPCATEGORY HG ON (HR.HPCATE_CODE = HG.HPCATE_CODE)


         AND HR.HP_NO=#{hpNo}


         AND H.HP_NAME LIKE '%' || #{hpName} || '%'


         AND HR.HPCATE_CODE=#{hpCateNo}


         AND HG.HPCATE_NAME LIKE '%' || #{hpCateName} || '%'


         AND C.C_NO=#{cNo}


         AND HR.RECEIPT_YN=#{approval}

   )

SELECT ROWNUM, HP_RE_NO, HP_NAME, HPCATE_CODE, HPCATE_NAME, C_NO, C_ID, HP_NO, HP_RE_DATE, LIKE_YN, HP_RE_AVG_RATE, RECEIPT_YN FROM (SELECT HR.HP_RE_NO, H.HP_NAME, HR.HPCATE_CODE, HG.HPCATE_NAME, C.C_NO, C.C_ID, HR.HP_NO, HR.HP_RE_DATE, HR.LIKE_YN, HR.HP_RE_AVG_RATE, HR.RECEIPT_YN FROM HPREVIEW HR JOIN CLIENT C ON(HR.C_NO = C.C_NO) JOIN HOSPITAL H ON (HR.HP_NO = H.HP_NO) JOIN HPCATEGORY HG ON (HR.HPCATE_CODE = HG.HPCATE_CODE)
AND HR.HP_NO=#{hpNo}
AND H.HP_NAME LIKE '%' || #{hpName} || '%'
AND HR.HPCATE_CODE=#{hpCateNo}
AND HG.HPCATE_NAME LIKE '%' || #{hpCateName} || '%'
AND C.C_NO=#{cNo}
AND HR.RECEIPT_YN=#{approval}
ORDER BY HR.HP_RE_DATE DESC)

mapper.update:
UPDATE HPREVIEW
SET RECEIPT_YN='Y'
WHERE HP_RE_NO=#{hpReNo}
