mapper.select:
  SELECT COUNT(*)
  FROM 
  (SELECT HR.HP_RE_NO, H.HP_NAME, HL.HPCATE_CODE, HG.HPCATE_NAME, HR.C_NO, C.C_ID, HR.HP_NO, HP_RE_DATE, LIKE_YN, HR.HP_RE_AVG_RATE, RECEIPT_YN
FROM HPREVIEW HR
    JOIN CLIENT C ON (HR.C_NO = C.C_NO)
    JOIN HOSPITAL H ON (HR.HP_NO = H.HP_NO)
    JOIN HPLIST HL ON (H.HP_NO = HL.HP_NO)
    JOIN HPCATEGORY HG ON (HL.HPCATE_CODE = HG.HPCATE_CODE)


      AND HR.HP_NO=#{hpNo}


      AND H.HP_NAME LIKE '%' || #{hpName} || '%'


      AND HL.HPCATE_CODE=#{hpCateNo}


      AND HG.HPCATE_NAME LIKE '%' || #{hpCateName} || '%'


      AND C.C_NO=#{cNo}


      AND HR.RECEIPT_YN=#{approval}

)

  SELECT *
  FROM 
  (SELECT HR.HP_RE_NO, H.HP_NAME, HL.HPCATE_CODE, HG.HPCATE_NAME, HR.C_NO, C.C_ID, HR.HP_NO, HP_RE_DATE, LIKE_YN, HR.HP_RE_AVG_RATE, RECEIPT_YN
FROM HPREVIEW HR
    JOIN CLIENT C ON (HR.C_NO = C.C_NO)
    JOIN HOSPITAL H ON (HR.HP_NO = H.HP_NO)
    JOIN HPLIST HL ON (H.HP_NO = HL.HP_NO)
    JOIN HPCATEGORY HG ON (HL.HPCATE_CODE = HG.HPCATE_CODE)


      AND HR.HP_NO=#{hpNo}


      AND H.HP_NAME LIKE '%' || #{hpName} || '%'


      AND HL.HPCATE_CODE=#{hpCateNo}


      AND HG.HPCATE_NAME LIKE '%' || #{hpCateName} || '%'


      AND C.C_NO=#{cNo}


      AND HR.RECEIPT_YN=#{approval}

)
ORDER BY 1
