mapper.select:
SELECT OQ.OQNA_NO, OM.ORDER_NO,P.PD_NAME,OQ.OQNA_TITLE,C.C_ID,C.C_NO,D.DR_NO,D.DR_ID,OQ.OQNA_CONTENT,OQ.OQNA_DATE,OQ.OQNA_STATUS,OQ.OQNA_ANSWER FROM ORDERQNA OQ LEFT JOIN ORDERMG OM ON(OQ.ORDER_NO=OM.ORDER_NO) LEFT JOIN ORDERPRODUCT OP ON(OM.ORDER_NO=OP.ORDER_NO) LEFT JOIN PRODUCT P ON(OP.PD_NO=P.PD_NO) LEFT JOIN CLIENT C ON(OM.C_NO=C.C_NO) LEFT JOIN DRCLIENT D ON(OM.DR_NO=D.DR_NO)
WHERE OM.ORDER_NO = #{search}
WHERE C.C_ID =#{search} OR D.DR_ID=#{search}
WHERE OQ.OQNA_TITLE LIKE '%'||#{search}||'%'
WHERE OQ.OQNA_CONTENT LIKE '%'||#{search}||'%'
