mapper.select:
SELECT COUNT(*) FROM
PRODUCTREVIEW
WHERE C_NO=#{cNo}

SELECT COUNT(*) FROM PAYMENT PA JOIN ORDERMG OM ON(OM.ORDER_NO=PA.ORDER_NO) WHERE PA.C_NO=#{cNo}
AND (OM.O_CODE BETWEEN 1 AND 5 OR OM.O_CODE =14)
AND OM.O_CODE BETWEEN 6 AND 13

SELECT *
FROM PRODUCTREVIEW
WHERE ORDER_NO=#{orderNo} AND C_NO=#{cNo} AND PD_NO=#{pdNo}

SELECT NVL(SUM(use_point),0) AS ALLSUMUSEPOINT
FROM PAYMENT
WHERE C_NO=#{cNo}

SELECT C_POINT
FROM CLIENT
WHERE C_NO=#{cNo}

SELECT PRP_RENAME
FROM PRODUCTREVIEW
WHERE ORDER_NO=#{orderNo} AND C_NO=#{cNo} AND PD_NO=#{pdNo}
