mapper.select:
SELECT COUNT(*) FROM
PRODUCTREVIEW
WHERE DR_NO=#{dNo}

SELECT COUNT(*) FROM PAYMENT PA JOIN ORDERMG OM ON(OM.ORDER_NO=PA.ORDER_NO) WHERE PA.DR_NO=#{drNo}
AND OM.O_CODE BETWEEN 1 AND 5 OR OM.O_CODE =14
AND OM.O_CODE BETWEEN 6 AND 13

SELECT *
FROM PRODUCTREVIEW
WHERE ORDER_NO=#{orderNo} AND DR_NO=#{dNo}

SELECT NVL(SUM(use_point),0) AS ALLSUMUSEPOINT
FROM PAYMENT
WHERE DR_NO=#{drNo}

SELECT DR_POINT
FROM DRCLIENT
WHERE DR_NO=#{drNo}

SELECT PRO_RENAME
FROM PROFILEPHOTO
WHERE DR_NO=#{drNo}

SELECT COUNT(*)
FROM HPLIKE

WHERE DR_NO=#{dNo}


WHERE C_NO=#{cNo}
	AND DR_NO=#{dNo}

mapper.delete:
DELETE FROM HPLIKE
WHERE DR_NO=#{dNo}
	AND C_NO=#{cNo}

mapper.select:
SELECT COUNT(*)
FROM HPLIKE
WHERE DR_NO=#{dNo}

SELECT COUNT(*)
FROM HPLIKE
WHERE DR_NO=#{drNo}

SELECT COUNT(*)
FROM ASKDRREPLY
WHERE DR_NO=#{drNo}

SELECT COUNT(*)
FROM DRCOMMENT
WHERE DR_NO=#{drNo}

SELECT PRP_RENAME
FROM PRODUCTREVIEW
WHERE ORDER_NO=#{orderNo} AND DR_NO=#{dNo}

SELECT COUNT(*)
FROM DRCOMMENT
WHERE DR_NO=#{drNo}
