mapper.select:
SELECT *
FROM PRODUCTREVIEW
WHERE ORDER_NO=#{orderNo} AND DR_NO=#{dNo}

SELECT NVL(SUM(use_point),0) AS ALLSUMUSEPOINT
FROM PAYMENT
WHERE DR_NO=#{drNo}

SELECT DR_POINT
FROM DRCLIENT
WHERE DR_NO=#{drNo}

SELECT PRP_RENAME
FROM PRODUCTREVIEW
WHERE ORDER_NO=#{orderNo} AND DR_NO=#{dNo}
