mapper.select:
SELECT *
FROM CLIENT
WHERE C_ID=#{userId}

SELECT *
FROM DRCLIENT
WHERE DR_ID=#{userId}

SELECT *
FROM Client
WHERE C_NAME=#{userName}
AND
C_EMAIL=#{email}

SELECT *
FROM DrClient
WHERE DR_NAME=#{userName}
AND
DR_EMAIL=#{email}

SELECT *
FROM DRCLIENT
WHERE DR_EMAIL = #{email}

SELECT * 
FROM CLIENT
WHERE C_ID = #{userId}
AND C_EMAIL = #{email}

SELECT * 
FROM DRCLIENT
WHERE DR_ID = #{userId}
AND DR_EMAIL = #{email}

SELECT * 
FROM CLIENT
WHERE C_NO = #{cNo}
AND C_EMAIL = #{email}

SELECT *
FROM DRCLIENT
WHERE DR_NO = #{drNo}
AND DR_EMAIL = #{email}

SELECT * 
FROM CLIENT
WHERE C_NO = #{cNo}

SELECT *
FROM DRCLIENT
WHERE DR_NO=#{drNo}

mapper.update:
UPDATE CLIENT
SET 
	C_PWD = #{userPwd}
WHERE C_NO = #{cNo}

UPDATE DRCLIENT
SET 
	DR_PWD = #{userPwd}
WHERE DR_NO = #{drNo}

mapper.select:
SELECT * 
FROM CLIENT
WHERE C_ID = #{userId}

SELECT * 
FROM DRCLIENT
WHERE DR_ID = #{userId}

mapper.update:
UPDATE PROFILEPHOTO
SET PRO_ORIGIN = #{proOrigin},
		PRO_RENAME = #{proRename}
WHERE C_NO = #{cNo}

UPDATE PROFILEPHOTO
SET PRO_ORIGIN = #{proOrigin},
		PRO_RENAME = #{proRename}
WHERE DR_NO = #{drNo}

UPDATE DRCLIENT
SET DR_ID = #{userId},
	  DR_EMAIL = #{email},
	  DR_PHONE = #{phone},
	  DR_BIRTH=#{birth}
WHERE DR_NO = #{drNo}

UPDATE CLIENT
SET C_STATUS = 'N'
WHERE C_NO = #{cNo}

UPDATE DRCLIENT
SET DR_STATUS = 'N'
WHERE DR_NO = #{drNo}

UPDATE DRHPPHOTO
SET DRHP_ORIGIN = #{drhpOrigin},
	  DRHP_RENAME = #{drhpRename}
WHERE DR_NO = #{drNo}
AND PH_CODE = 1

UPDATE DRHPPHOTO
SET DRHP_ORIGIN = #{drhpOrigin},
	  DRHP_RENAME = #{drhpRename}
WHERE DR_NO = #{drNo}
AND PH_CODE = 2

UPDATE DRHPPHOTO
SET DRHP_ORIGIN = #{drhpOrigin},
	  DRHP_RENAME = #{drhpRename}
WHERE DR_NO = #{drNo}
AND PH_CODE = 3

UPDATE DRCLIENT
SET DR_APPROVAL = 'N',
	  HP_NO = #{hpNo},
	  HPCATE_CODE = #{hpCateCode}
WHERE DR_NO = #{drNo}

UPDATE CLIENT
SET C_PROFILE = 'Y'
WHERE C_NO = #{cNo}

UPDATE DRCLIENT
SET DR_PROFILE = 'Y'
WHERE DR_NO = #{drNo}
