mapper.select:
SELECT * FROM (SELECT ROWNUM AS RN, AD_NO, AD_TITLE, C_NICKNAME, AD_SUBMITDATE, AD_CHOOSESTATUS FROM (SELECT AD.AD_NO, AD.AD_TITLE, C.C_NICKNAME, AD.AD_SUBMITDATE, AD.AD_CHOOSESTATUS FROM ASKDR AD JOIN CLIENT C ON (AD.C_NO = C.C_NO) WHERE HP_CATE_CODE = #{categoryNo}
AND AD.AD_CHOOSESTATUS = #{choosed}
ORDER BY 4 DESC) )

SELECT COUNT(*) 
FROM ASKDR
WHERE HP_CATE_CODE = #{categoryNo}

	AND AD_CHOOSESTATUS = #{choosed}

SELECT COUNT(*) FROM ASKDR
WHERE AD_TITLE LIKE '%' || #{searchBoardContent} || '%'
WHERE AD_SYMPTOM LIKE '%' || #{searchBoardContent} || '%'
JOIN CLIENT C ON (ASKDR.C_NO = C.C_NO) WHERE C.C_NICKNAME LIKE '%' || #{searchBoardContent} || '%'
AND HP_CATE_CODE = #{category}
AND AD_CHOOSESTATUS = #{choosed}

SELECT RN, AD_NO, AD_TITLE, C_NICKNAME, AD_SUBMITDATE, AD_CHOOSESTATUS FROM (SELECT ROWNUM AS RN, AD_NO, AD_TITLE, C_NICKNAME, AD_SUBMITDATE, AD_SYMPTOM, AD_CHOOSESTATUS FROM (SELECT AD.AD_NO, AD.AD_TITLE, C.C_NICKNAME, AD.AD_SUBMITDATE, AD.AD_SYMPTOM, AD.AD_CHOOSESTATUS FROM ASKDR AD JOIN CLIENT C ON (AD.C_NO = C.C_NO) WHERE HP_CATE_CODE = #{category}
AND AD_CHOOSESTATUS = #{choosed}
ORDER BY 4 DESC )
WHERE AD_TITLE LIKE '%' || #{searchBoardContent} || '%'
WHERE AD_SYMPTOM LIKE '%' || #{searchBoardContent} || '%'
WHERE C_NICKNAME LIKE '%' || #{searchBoardContent} || '%'
)

mapper.update:
UPDATE ASKDRREPLY
SET ADR_CHOOSESTATUS = 'Y'
WHERE ADR_NO = #{adrNo}

UPDATE ASKDR
SET AD_CHOOSESTATUS = 'Y'
WHERE AD_NO = #{bNo}

UPDATE ASKDRREPLY
SET
    ADR_CONTENT = #{content}
WHERE
    ADR_NO = #{adrNo}

UPDATE CLIENT
SET C_POINT = C_POINT + 50
WHERE C_NO = #{cNo}

UPDATE DRCLIENT
SET DR_POINT = DR_POINT + 50
WHERE DR_NO = #{drNo}
